const BookShelfChanger = ({ shelf }) => {
    const selectedShelf = (shelf) ? shelf : 'none';

    const options = [
        { value: 'moveTo', label: 'Move to...' },
        { value: 'currentlyReading', label: 'Currently Reading' },
        { value: 'wantToRead', label: 'Want to Read' },
        { value: 'read', label: 'Read' },
        { value: 'none', label: 'None' }
    ];

    const optionIsDisabled = (option) => {
        return option.value === "moveTo";
    }


    return (
        <div className="book-shelf-changer">
            <select>
                {options.map((option) => {
                    return (
                        <option value={option.value} disabled={optionIsDisabled(option)} selected={option.value === selectedShelf}>{option.label}</option>
                    );
                })}
            </select>


