import { Link } from "react-router-dom";
import Book from "./Book";
import Shelf from "./Shelf";

const BookList = ({ books, onBookChangeShelf }) => {
    return (
        <div className="list-books">
        <div className="list-books-title">
          <h1>MyReads</h1>
        </div>
        <div className="list-books-content">
          <div>

            <Shelf books={books} heading='Currently Reading' shelf='currentlyReading' onBookChangeShelf={onBookChangeShelf} />



            <div className="bookshelf">
              <h2 className="bookshelf-title">Want to Read</h2>
              <div className="bookshelf-books">
                <ol className="books-grid">

                {books.map((book) => {
                        if (book.shelf === "wantToRead")
                            return (
                                <li key={book.id}>
                                <Book
                                id={book.id}
                                title={book.title}
                                authors={book.authors}
                                thumbnailUrl={book.imageLinks.thumbnail}
                                shelf={book.shelf}
                                onBookChangeShelf={onBookChangeShelf}
                                />
                            </li>
                            );
                    })}

                </ol>
              </div>
            </div>
            <div className="bookshelf">
              <h2 className="bookshelf-title">Read</h2>
              <div className="bookshelf-books">
                <ol className="books-grid">

                {books.map((book) => {
                    if (book.shelf === "read")
                        return (
                            <li key={book.id}>
                            <Book
                            id={book.id}
                            title={book.title}
                            authors={book.authors}
                            thumbnailUrl={book.imageLinks.thumbnail}
                            shelf={book.shelf}
                            onBookChangeShelf={onBookChangeShelf}
                            />
                        </li>
                        );
                })}

                </ol>
              </div>
            </div>
          </div>
        </div>

        <div className="open-search">
          <Link to="/search">Add a book</Link>
        </div>

      </div>

    );
};

export default BookList;
