const BookShelfChanger = ({ shelf, onShelfSelect }) => {
    const selectedShelf = (shelf) ? shelf : 'none';

    const options = [
        { value: 'moveTo', label: 'Move to...' },
        { value: 'currentlyReading', label: 'Currently Reading' },
        { value: 'wantToRead', label: 'Want to Read' },
        { value: 'read', label: 'Read' },
        { value: 'none', label: 'None' }
    ];

    const optionIsDisabled = (option) => {
        return option.value === "moveTo";
    }

    const optionIsSelected = (option) => {
        return option.value === selectedShelf;
    }

    return (
        <div className="book-shelf-changer">
            <select>
                {options.map((option) => {
                    return (
                      <option
                        onSelect={onShelfSelect}
                        value={option.value}
                        disabled={optionIsDisabled(option)}
                        selected={optionIsSelected(option)}
                      >
                        {option.label}
                      </option>
                    );
                })}
            </select>
      </div>
    );
}

export default BookShelfChanger;
