import { useState } from "react";
import { Link } from "react-router-dom";
import Book from "./Book";
import * as BooksAPI from "./BooksAPI";

const Search = ({ books, onBookChangeShelf }) => {
    const [catalog, setCatalog] = useState([]);

    const searchCatalog = (query, handleSearchResult) => {
        const search = async (query) => {
            if (query === "") return [];
            const res = await BooksAPI.search(query);
            handleSearchResult(res);
        }
        search(query);
      };

    const handleSearchResult = (res) => {
        setCatalog(res);
    }

    const handleQueryChange = (event) => {
            searchCatalog(event.target.value, handleSearchResult);
    };



    return (
      <div className="search-books">
        <div className="search-books-bar">
          <Link to="/" className="close-search">
            Close
          </Link>

          <div className="search-books-input-wrapper">
            <input
              type="text"
              placeholder="Search by title, author, or ISBN"
              onChange={handleQueryChange}
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            {catalog.map((book) => {
                return (
                    <li key={book.id}>
                        <Book book={book} onBookChangeShelf={onBookChangeShelf} />
                    </li>
                );
            })}
          </ol>
        </div>
      </div>
    );

};

export default Search;
